import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './assets/styles/styles.css';

const App = () => {
  const [stats, setStats] = useState(null);
  const [selectedOption, setSelectedOption] = useState('');
  const [isOpen, setIsOpen] = useState(false);
  const [isTextActive, setIsTextActive] = useState(false);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const response = await axios.get(`/api/${selectedOption.toLowerCase()}`);
        setStats(response.data);
      } catch (error) {
        console.error('Error fetching stats:', error.message);
      }
    };

    fetchStats();
  }, [selectedOption]);

  const toggleDropdown = () => {
    setIsOpen(!isOpen);
  };

  const handleSelect = (option) => {
    setSelectedOption(option);
    setIsOpen(false);

    setIsTextActive(false); 
    void document.querySelector('.custom-dropdown-selected-text')?.offsetWidth;
    setIsTextActive(true);
  };

  return (
    <div>
      <h1>How's The Market</h1>
      <div className="dropdown-container">
        <div className="custom-dropdown" onClick={toggleDropdown}>
          <div className="custom-dropdown-selected">
            <span className="arrow-down">&#9662;</span>
            <span
              className={`custom-dropdown-selected-text ${
                isTextActive ? 'active' : ''
              }`}
            >
              {selectedOption}
            </span>
          </div>
          {isOpen && (
            <div className="custom-dropdown-options">
              <div className="custom-dropdown-option" onClick={() => handleSelect('Today')}>
                Today
              </div>
              <div className="custom-dropdown-option" onClick={() => handleSelect('This Week')}>
                This Week
              </div>
              <div className="custom-dropdown-option" onClick={() => handleSelect('This Month')}>
                This Month
              </div>
            </div>
          )}
        </div>
        <span className="question-mark">?</span>
      </div>
    </div>
  );
};

export default App;
