import React, { useEffect, useState } from 'react';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, BarElement, Title, Tooltip, Legend } from 'chart.js';
import './assets/styles/styles.css';
import axios from 'axios';

ChartJS.register(CategoryScale, LinearScale, PointElement, BarElement, Title, Tooltip, Legend);

const App = () => {
  const [status, setStatus] = useState('positive');
  const [selectedOption, setSelectedOption] = useState('');
  const [isOpen, setIsOpen] = useState(false);
  const [textKey, setTextKey] = useState(0);
  const [data, setData] = useState(null); 
  const [error, setError] = useState(null); 

  useEffect(() => {
    const root = document.documentElement;
    root.style.setProperty('--primary-color', status === 'positive' ? 'green' : 'firebrick');
  }, [status]);

  useEffect(() => {
    if (!selectedOption) return; // Don't fetch until an option is selected

    let timeframe;
    if (selectedOption === "Today") {
      timeframe = "day";
    }
    else if (selectedOption === "This Week") {
      timeframe = "week";
    }
    else if (selectedOption === "This Month") {
      timeframe = "month";
    }

    const fetchData = async () => {
      setIsLoading(true);
      setError(null);
      try {
        const response = await axios.get(`http://localhost:5001/api/${timeframe}`);
        setData(response.data);
      } catch (err) {
        setError(err.message || 'Something went wrong!');
      } finally {
        setIsLoading(false);
      }
    };

    fetchData();
  }, [selectedOption]);

  const chartData = data ? {
    labels: Object.values(data).map(entry => entry.date).reverse(),  
    datasets: [
      {
        label: 'Job Count',
        data: Object.values(data).map(entry => entry.job_count).reverse(),  
        backgroundColor: Object.values(data).reverse().map((entry, index, array) => {
          if (index === 0) {
            return 'green';  // First bar is gray (no comparison to the previous value)
          }
          // Compare current job_count with the previous one
          const previousJobCount = array[index - 1].job_count; // Get the previous job_count
          return entry.job_count < previousJobCount ? 'rgb(178, 34, 34)' : 'rgb(0, 128, 0)';  // Red if lower, green if higher
        }),
        borderColor: 'rgba(75, 192, 192, 1)',
        borderWidth: 1,
      },
    ],
  } : {};

  const chartOptions = {
    maintainAspectRatio: false,
    responsive: true,
    plugins: {
      title: {
        display: true,
        text: 'New Job Postings',
        font: {
          size: window.innerWidth < 767 ? 16 : 24,  // Dynamic font size (adjust based on screen width)
          family: 'Arial, sans-serif',  // Font family
          weight: 'bold',  // Font weight
        },
        color: '#333',  // Font color (can be hex, rgb, etc.)
        padding: {
          top: 20,  // Adjust padding around the title
          bottom: 30,
        },
      },
      legend: {
        display: false,
      },
      tooltip: {
        callbacks: {
          label: (tooltipItem) => {
            return `Job Count: ${tooltipItem.raw}`;
          },
        },
      },
    },
    scales: {
      x: {
        grid: {
          display: false, 
        },
      },
    },
  };

  const toggleDropdown = () => {
    setIsOpen(!isOpen);
  };

  const handleSelect = (option) => {
    setSelectedOption(option);
    setIsOpen(false);

    if (option == 'Today') {
      setStatus('positive')
    }
    else {
      setStatus('negative')
    }

    setTextKey((prevKey) => prevKey + 1);
  };

  return (
    <div>
      <h1>How's The Market</h1>
      <div className="dropdown-container">
        <div className="custom-dropdown" onClick={toggleDropdown}>
          <div className="custom-dropdown-selected">
            <span className={`arrow-down ${isOpen ? 'rotate-down' : 'rotate-right'}`}>&#9662;</span>
            <span key={textKey} className="custom-dropdown-selected-text animate"> {selectedOption}</span>
          </div>
          <div className={`custom-dropdown-options ${isOpen ? 'open' : ''}`}>
            <div className="custom-dropdown-option" onClick={() => handleSelect('Today')}>
              Today
            </div>
            <div className="custom-dropdown-option" onClick={() => handleSelect('This Week')}>
              This Week
            </div>
            <div className="custom-dropdown-option" onClick={() => handleSelect('This Month')}>
              This Month
            </div>
          </div>
        </div>
        <span className="question-mark">?</span>
      </div>

      <div>
        {error && <p>Error: {error}</p>}
        {data && (
          <div className="chart-container"> 
            <Bar data={chartData} options={chartOptions} />
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
