import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './assets/styles/styles.css'; // Make sure your styles are imported

const App = () => {
  const [stats, setStats] = useState(null);
  const [selectedOption, setSelectedOption] = useState('Today');

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const response = await axios.get(`/api/${selectedOption.toLowerCase()}`);
        setStats(response.data);
      } catch (error) {
        console.error('Error fetching stats:', error.message);
      }
    };

    fetchStats();
  }, [selectedOption]);

  const handleChange = (event) => {
    setSelectedOption(event.target.value);
  };

  return (
    <div>
      <h1>How's The Market</h1>

      {/* Dropdown Menu with Underline and Question Mark */}
      <div className="dropdown-container">
        <label htmlFor="timeframe" className="dropdown-label">
          <span>What time period?</span>
        </label>
        <div className="dropdown-with-underline">
          <select
            id="timeframe"
            value={selectedOption}
            onChange={handleChange}
            className="dropdown-select"
          >
            <option value="Today">Today</option>
            <option value="This Week">This Week</option>
            <option value="This Month">This Month</option>
          </select>
          <span className="question-mark">?</span>
        </div>
      </div>

      {/* Display stats */}
      {stats && (
        <div>
          <h2>Stats for {selectedOption}</h2>
          {/* Display your stats here */}
          <pre>{JSON.stringify(stats, null, 2)}</pre>
        </div>
      )}
    </div>
  );
};

export default App;
