import React, { useEffect, useState } from 'react';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, BarElement, Title, Tooltip, Legend } from 'chart.js';
import './assets/styles/styles.css';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

const App = () => {
  const [status, setStatus] = useState('positive');
  const [selectedOption, setSelectedOption] = useState('');
  const [isOpen, setIsOpen] = useState(false);
  const [textKey, setTextKey] = useState(0);
  const [data, setData] = useState({
    labels: ['January', 'February', 'March', 'April', 'May', 'June', 'July'],
    datasets: [
      {
        label: 'Market Trends',
        data: [65, 59, 80, 81, 56, 55, 40],
        borderColor: 'rgba(75, 192, 192, 1)',
        backgroundColor: 'rgba(75, 192, 192, 0.2)',
        fill: true,
      },
    ],
  });

  useEffect(() => {
    // Update the CSS custom property based on the status
    const root = document.documentElement;
    root.style.setProperty('--primary-color', status === 'positive' ? 'green' : 'firebrick');
  }, [status]);

  const toggleDropdown = () => {
    setIsOpen(!isOpen);
  };

  const handleSelect = (option) => {
    setSelectedOption(option);
    setIsOpen(false);

    if (option == 'Today') {
      setStatus('positive')
    }
    else {
      setStatus('negative')
    }

    setTextKey((prevKey) => prevKey + 1);
  };

  return (
    <div>
      <h1>How's The Market</h1>
      <div className="dropdown-container">
        <div className="custom-dropdown" onClick={toggleDropdown}>
          <div className="custom-dropdown-selected">
            <span className={`arrow-down ${isOpen ? 'rotate-down' : 'rotate-right'}`}>&#9662;</span>
            <span key={textKey} className="custom-dropdown-selected-text animate"> {selectedOption}</span>
          </div>
          <div className={`custom-dropdown-options ${isOpen ? 'open' : ''}`}>
            <div className="custom-dropdown-option" onClick={() => handleSelect('Today')}>
              Today
            </div>
            <div className="custom-dropdown-option" onClick={() => handleSelect('This Week')}>
              This Week
            </div>
            <div className="custom-dropdown-option" onClick={() => handleSelect('This Month')}>
              This Month
            </div>
          </div>
        </div>
        <span className="question-mark">?</span>
      </div>

      <div style={{ width: '50%', margin: '0 auto' }}>
        <Bar data={data} options={{ responsive: true }} />
      </div>

      <button onClick={() => setStatus(status === 'positive' ? 'negative' : 'positive')}>
        Toggle Status
      </button>

    </div>
  );
};

export default App;
