import React, { useEffect, useState } from 'react';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from 'chart.js';
import './assets/styles/styles.css';
import axios from 'axios';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

const App = () => {
  const [status, setStatus] = useState('positive');
  const [selectedOption, setSelectedOption] = useState('');
  const [isOpen, setIsOpen] = useState(false);
  const [textKey, setTextKey] = useState(0);
  const [data, setData] = useState(null); 
  const [isLoading, setIsLoading] = useState(false); 
  const [error, setError] = useState(null); 

  useEffect(() => {
    // Update the CSS custom property based on the status
    const root = document.documentElement;
    root.style.setProperty('--primary-color', status === 'positive' ? 'green' : 'firebrick');
  }, [status]);

  useEffect(() => {
    if (!selectedOption) return; // Don't fetch until an option is selected

    const fetchData = async () => {
      setIsLoading(true);
      setError(null);
      try {
        const response = await axios.get(`http://localhost:5001/api/${selectedOption}`);
        setData(response.data);
      } catch (err) {
        setError(err.message || 'Something went wrong!');
      } finally {
        setIsLoading(false);
      }
    };

    fetchData();
  }, [selectedOption]);


  const toggleDropdown = () => {
    setIsOpen(!isOpen);
  };

  const handleSelect = (option) => {
    setSelectedOption(option);
    setIsOpen(false);

    if (option == 'Today') {
      setStatus('positive')
    }
    else {
      setStatus('negative')
    }

    setTextKey((prevKey) => prevKey + 1);
  };

  return (
    <div>
      <h1>How's The Market</h1>
      <div className="dropdown-container">
        <div className="custom-dropdown" onClick={toggleDropdown}>
          <div className="custom-dropdown-selected">
            <span className={`arrow-down ${isOpen ? 'rotate-down' : 'rotate-right'}`}>&#9662;</span>
            <span key={textKey} className="custom-dropdown-selected-text animate"> {selectedOption}</span>
          </div>
          <div className={`custom-dropdown-options ${isOpen ? 'open' : ''}`}>
            <div className="custom-dropdown-option" onClick={() => handleSelect('Today')}>
              Today
            </div>
            <div className="custom-dropdown-option" onClick={() => handleSelect('This Week')}>
              This Week
            </div>
            <div className="custom-dropdown-option" onClick={() => handleSelect('This Month')}>
              This Month
            </div>
          </div>
        </div>
        <span className="question-mark">?</span>
      </div>

      <div>
        <canvas id="myChart"></canvas>
        HELLO
      </div>

      <script src="https://cdn.jsdelivr.net/npm/chart.js"></script>
      <script src="./frontend/components/Graphs.js"></script>

    </div>
  );
};

export default App;
