import React, { useEffect, useState } from 'react';
import { Bar, Line } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, BarElement, LineElement, Title, Tooltip, Legend } from 'chart.js';
import './assets/styles/styles.css';
import axios from 'axios';

ChartJS.register(CategoryScale, LinearScale, PointElement, BarElement, LineElement, Title, Tooltip, Legend);

const App = () => {
  const [status, setStatus] = useState('positive');
  const [selectedOption, setSelectedOption] = useState('');
  const [isOpen, setIsOpen] = useState(false);
  const [textKey, setTextKey] = useState(0);
  const [data, setData] = useState({}); 
  const [error, setError] = useState(null); 
  const [upOrDown, setUpOrDown] = useState(null); 
  const [percentageChange, setPercentageChange] = useState(null);

  useEffect(() => {
    if (!selectedOption) return; // Don't fetch until an option is selected

    let timeframe;
    if (selectedOption === "Today") {
      timeframe = "day";
    }
    else if (selectedOption === "This Week") {
      timeframe = "week";
    }
    else if (selectedOption === "This Month") {
      timeframe = "month";
    }

    const fetchData = async () => {
      setError(null);
      try {
        const response = await axios.get(`http://localhost:5001/api/${timeframe}`);
        setData(response.data);
      } catch (err) {
        setError(err.message || 'Something went wrong!');
      }
    };

    fetchData();
  }, [selectedOption]);

  useEffect(() => {
    if (data == {}) {
      setPercentageChange(0);
      setUpOrDown("Up");
      return;
    }
    if (data["0"] && data["1"]) {
      const change = (((data["0"]["job_count"] - data["1"]["job_count"]) / data["1"]["job_count"]) * 100).toFixed(1);
      setPercentageChange(change);
      setUpOrDown(change > 0 ? "Up" : "Down");
      const root = document.documentElement;
      root.style.setProperty('--primary-color', change < 0 ? 'firebrick' : 'green');
    }
  }, [data]); 

  const getTodayLabels = () => {
    const daysOfWeek = ["Sun", "Mon", "Tues", "Wed", "Thurs", "Fri", "Sat"];

    const today = new Date();
    const currentDay = today.getDay();  // 0 (Sunday) to 6 (Saturday)

    // Generate the labels array
    let labels = [];
    for (let i = 0; i < Object.keys(data).length; i++) {
      // Calculate the index for each day to shift right to left
      const labelIndex = (currentDay - Object.keys(data).length + 8+ i) % 7;
      labels.push(daysOfWeek[labelIndex]);
    }

    return labels;
  };

  const getWeekLabels = () => {
    return Object.values(data).map(entry => entry.date).reverse();
  };

  const getMonthLabels = () => {
    const months = ["Jan", "Feb", "Mar", "Apr", "May", "June", "July", "Aug", "Sept", "Oct", "Nov", "Dec"];

    const today = new Date();
    const currentDay = today.getMonth();  // 0 (January) to 11 (December)

    let labels = [];
    for (let i = 0; i < Object.keys(data).length; i++) {
      // Calculate the index for each day to shift right to left
      const labelIndex = (currentDay - Object.keys(data).length + 13 + i) % 12;
      labels.push(months[labelIndex]);
    }

    return labels;
  };

  const chartData = data ? {
    labels: Object.values(data).map(entry => entry.date).reverse(),  
    datasets: [
      {
        label: 'Job Count',
        data: Object.values(data).map(entry => entry.job_count).reverse(),  
        backgroundColor: Object.values(data).reverse().map((entry, index, array) => {
          if (index === 0) {
            return 'rgba(0, 128, 0, 0.85)';  // First bar is gray (no comparison to the previous value)
          }
          // Compare current job_count with the previous one
          const previousJobCount = array[index - 1].job_count; // Get the previous job_count
          return entry.job_count < previousJobCount ? 'rgba(178, 34, 34, 0.85)' : 'rgba(0, 128, 0, 0.85)';  // Red if lower, green if higher
        }),
        borderColor: 'rgba(75, 192, 192, 1)',
        borderWidth: 1,
      },
    ],
  } : {};

  const chartOptions = {
    maintainAspectRatio: false,
    responsive: true,
    plugins: {
      legend: {
        display: false,
      },
      tooltip: {
        callbacks: {
          label: (tooltipItem) => {
            return `Job Count: ${tooltipItem.raw}`;
          },
        },
      },
    },
    scales: {
      x: {
        grid: {
          display: false, 
        },
        labels: selectedOption === "Today" ? getTodayLabels() : 
        (selectedOption === "This Week" ? getWeekLabels() :
        (selectedOption === "This Month" ? getMonthLabels() : [])),
      },
    },
  };

  const toggleDropdown = () => {
    setIsOpen(!isOpen);
  };

  const handleSelect = (option) => {
    setSelectedOption(option);
    setIsOpen(false);

    if (option == 'Today') {
      setStatus('positive')
    }
    else {
      setStatus('negative')
    }

    setTextKey((prevKey) => prevKey + 1);
  };

  return (
    <div>
      <h1>How's The Market</h1>
      <div className="dropdown-container">
        <div className="custom-dropdown" onClick={toggleDropdown}>
          <div className="custom-dropdown-selected">
            <span className={`arrow-down ${isOpen ? 'rotate-down' : 'rotate-right'}`}>&#9662;</span>
            <span key={textKey} className="custom-dropdown-selected-text animate"> {selectedOption}</span>
          </div>
          <div className={`custom-dropdown-options ${isOpen ? 'open' : ''}`}>
            <div className="custom-dropdown-option" onClick={() => handleSelect('Today')}>
              Today
            </div>
            <div className="custom-dropdown-option" onClick={() => handleSelect('This Week')}>
              This Week
            </div>
            <div className="custom-dropdown-option" onClick={() => handleSelect('This Month')}>
              This Month
            </div>
          </div>
        </div>
        <span className="question-mark">?</span>
      </div>

      <div>
        {error && <p>Error: {error}</p>}
        {upOrDown && selectedOption == "Today" && (
          <div> 
            <div className="stat">
              Job Postings Are {upOrDown} <span className="stat-percent">{Math.abs(percentageChange)}%</span> {selectedOption}
            </div>
            <div className="chart-container">
              <h1>New Job Postings</h1>
              <Bar data={chartData} options={chartOptions} />
            </div>
          </div>
        )}
        {selectedOption == "This Week" && (
          <div>
            <h1>More Data Required</h1>
            <h1>Available Dec. 1</h1>
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
