import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './assets/styles/styles.css'; // Make sure your styles are imported

const App = () => {
  const [stats, setStats] = useState(null);
  const [selectedOption, setSelectedOption] = useState('');

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const response = await axios.get(`/api/${selectedOption.toLowerCase()}`);
        setStats(response.data);
      } catch (error) {
        console.error('Error fetching stats:', error.message);
      }
    };

    fetchStats();
  }, [selectedOption]);

  const handleChange = (event) => {
    setSelectedOption(event.target.value);
  };

  return (
    <div>
      <h1>How's The Market</h1>
      <div className="dropdown-container">
        <div className="custom-dropdown">
          <div className="custom-dropdown-selected">
            <span>{selectedOption}</span>
            <span className="arrow-down">&#9662;</span>
          </div>
          <div className="custom-dropdown-options">
            <div className="custom-dropdown-option" onClick={() => setSelectedOption('Today')}>
              Today
            </div>
            <div className="custom-dropdown-option" onClick={() => setSelectedOption('This Week')}>
              This Week
            </div>
            <div className="custom-dropdown-option" onClick={() => setSelectedOption('This Month')}>
              This Month
            </div>
          </div>
        </div>
      </div>

      {/* Display stats */}
      {stats && (
        <div>
          <h2>Stats for {selectedOption}</h2>
          {/* Display your stats here */}
          <pre>{JSON.stringify(stats, null, 2)}</pre>
        </div>
      )}
    </div>
  );
};

export default App;
