import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './assets/styles/styles.css';

const App = () => {
  const [stats, setStats] = useState(null);
  const [selectedOption, setSelectedOption] = useState('');
  const [isOpen, setIsOpen] = useState(false);
  const [textKey, setTextKey] = useState(0);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const response = await axios.get(`/api/${selectedOption.toLowerCase()}`);
        setStats(response.data);
      } catch (error) {
        console.error('Error fetching stats:', error.message);
      }
    };

    fetchStats();
  }, [selectedOption]);

  const toggleDropdown = () => {
    setIsOpen(!isOpen);
  };

  const handleSelect = (option) => {
    setSelectedOption(option);
    setIsOpen(false);

    setTextKey((prevKey) => prevKey + 1);
  };

  return (
    <div>
      <h1>How's The Market</h1>
      <div className="dropdown-container">
        <div className="custom-dropdown" onClick={toggleDropdown}>
          <div className="custom-dropdown-selected">
          <span
              className={`arrow-down ${isOpen ? 'rotate-down' : 'rotate-right'}`}
            >
              &#9662;
            </span>
            <span
              key={textKey} // Force React to remount with a new key
              className="custom-dropdown-selected-text animate"
            >
              {selectedOption}
            </span>
          </div>
          {isOpen && (
            <div
              className={`custom-dropdown-options ${isOpen ? 'open' : ''}`}
            >
              <div
                className="custom-dropdown-option"
                onClick={() => handleSelect('Today')}
              >
                Today
              </div>
              <div
                className="custom-dropdown-option"
                onClick={() => handleSelect('This Week')}
              >
                This Week
              </div>
              <div
                className="custom-dropdown-option"
                onClick={() => handleSelect('This Month')}
              >
                This Month
              </div>
            </div>
          )}
        </div>
        <span className="question-mark">?</span>
      </div>
    </div>
  );
};

export default App;
