import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './assets/styles/styles.css';

const App = () => {
  const [status, setStatus] = useState('positive');
  const [selectedOption, setSelectedOption] = useState('');
  const [isOpen, setIsOpen] = useState(false);
  const [textKey, setTextKey] = useState(0);

  useEffect(() => {
    // Update the CSS custom property based on the status
    const root = document.documentElement;
    root.style.setProperty('--primary-color', status === 'positive' ? 'green' : 'firebrick');
  }, [status]);

  const toggleDropdown = () => {
    setIsOpen(!isOpen);
  };

  const handleSelect = (option) => {
    setSelectedOption(option);
    setIsOpen(false);

    if (option == 'Today') {
      setStatus('positive')
    }
    else {
      setStatus('negative')
    }

    setTextKey((prevKey) => prevKey + 1);
  };

  return (
    <div>
      <h1>How's The Market</h1>
      <div className="dropdown-container">
        <div className="custom-dropdown" onClick={toggleDropdown}>
          <div className="custom-dropdown-selected">
          <span
              className={`arrow-down ${isOpen ? 'rotate-down' : 'rotate-right'}`}
            >
              &#9662;
            </span>
            <span
              key={textKey} // Force React to remount with a new key
              className="custom-dropdown-selected-text animate"
            >
              {selectedOption}
            </span>
          </div>
          <div className={`custom-dropdown-options ${isOpen ? 'open' : ''}`}>
            <div
              className="custom-dropdown-option"
              onClick={() => handleSelect('Today')}
            >
              Today
            </div>
            <div
              className="custom-dropdown-option"
              onClick={() => handleSelect('This Week')}
            >
              This Week
            </div>
            <div
              className="custom-dropdown-option"
              onClick={() => handleSelect('This Month')}
            >
              This Month
            </div>
          </div>
        </div>
        <span className="question-mark">?</span>
      </div>
    </div>
  );
};

export default App;
